import React, { useState } from "react";
import styles from "./ProductDetails.module.css";
import { useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import { authSelector } from "../../../../redux/reducers/authReducer";
import {
  doc,
  setDoc,
  deleteDoc,
  updateDoc,
  getDoc,
} from "firebase/firestore";
import { db } from "../../../../config/firebase";
import MinusIcon from "../../../UI/Icons/MinusIcon";
import PlusIcon from "../../../UI/Icons/PlusIcon";

const ProductDetails = ({ product, title, price, productId, onCart, quantity }) => {
  const [productAddingToCart, setProductAddingToCart] = useState(false);
  const [productRemovingFromCart, setProductRemovingCart] = useState(false);

  const navigate = useNavigate();
  const { user } = useSelector(authSelector);
  const uid = user?.uid;

  const addProductToCart = async () => {
    if (!uid) {
      navigate("/login");
      return;
    }
  
    setProductAddingToCart(true);
    try {
      const cartRef = doc(db, "users", uid, "cart", String(productId));
      await setDoc(cartRef, {
        productId: String(productId),
        title,
        price,
        quantity: 1,
      });
    } catch (error) {
      console.error("Error adding to cart:", error);
    } finally {
      setProductAddingToCart(false);
    }
  };
  
  

  const removeProduct = async () => {
    if (!uid) return;

    setProductRemovingCart(true);
    try {
      const cartRef = doc(db, "users", uid, "cart", productId);
      await deleteDoc(cartRef);
    } catch (error) {
      console.error("Error removing product:", error);
    } finally {
      setProductRemovingCart(false);
    }
  };

  const handleAdd = async () => {
    if (!uid) return;

    try {
      const cartRef = doc(db, "users", uid, "cart", productId);
      const snap = await getDoc(cartRef);
      if (snap.exists()) {
        const currentQty = snap.data().quantity;
        await updateDoc(cartRef, { quantity: currentQty + 1 });
      }
    } catch (error) {
      console.error("Error increasing quantity:", error);
    }
  };

  const handleRemove = async () => {
    if (!uid) return;

    try {
      const cartRef = doc(db, "users", uid, "cart", productId);
      const snap = await getDoc(cartRef);
      if (snap.exists()) {
        const currentQty = snap.data().quantity;
        if (currentQty > 1) {
          await updateDoc(cartRef, { quantity: currentQty - 1 });
        } else {
          await deleteDoc(cartRef);
        }
      }
    } catch (error) {
      console.error("Error decreasing quantity:", error);
    }
  };

  return (
    <div className={styles.productDetails}>
      <div className={styles.productName}>
        <p>{`${title.slice(0, 35)}...`}</p>
      </div>
      <div className={styles.productOptions}>
        <p>â‚¹ {price}</p>
        {onCart && (
          <div className={styles.quantityContainer}>
            <MinusIcon handleRemove={handleRemove} />
            {quantity}
            <PlusIcon handleAdd={handleAdd} />
          </div>
        )}
      </div>

      {!onCart ? (
        <button
          className={styles.addBtn}
          title="Add to Cart"
          disabled={productAddingToCart}
          onClick={addProductToCart}
        >
          {productAddingToCart ? "Adding" : "Add To Cart"}
        </button>
      ) : (
        <button
          className={styles.removeBtn}
          title="Remove from Cart"
          onClick={removeProduct}
          disabled={productRemovingFromCart}
        >
          {productRemovingFromCart ? "Removing" : "Remove From Cart"}
        </button>
      )}
    </div>
  );
};

export default ProductDetails;
