import { db } from '../../config/firebase';
import { collection, getDocs } from 'firebase/firestore';
import { setCartItems, setIsProductsLoaded } from '../reducers/cartReducer';

export const addProductsToCart = (product, userId) => async (dispatch) => {
  try {
    const querySnapshot = await getDocs(collection(db, `users/${userId}/cart`));
    const products = querySnapshot.docs.map(doc => ({
      id: doc.id,
      ...doc.data()
    }));
    
    products.push(product);
    dispatch(setCartItems(products));
    dispatch(setIsProductsLoaded(true));

    console.log(`✅ Product "${product.title || product.name}" added to cart successfully!`);
  } catch (error) {
    console.error("❌ Error fetching cart items:", error);
    dispatch(setIsProductsLoaded(false));
  }
};

  

export const removeProductFromCart = (productId) => async (dispatch, getState) => {
  try {
    const currentCart = getState().cartReducer.cart;
    const updatedCart = currentCart.filter(item => item.id !== productId);
    dispatch(setCartItems(updatedCart));
  } catch (error) {
    console.error("Error removing product from cart:", error);
  }
};
