      {!!cartItems?.length && (
        <aside className={styles.totalPrice}>
          <p>TotalPrice:- â‚¹{}/-</p>
          <button
            className={styles.purchaseBtn}
            
          >
            {purchasing ? "Purchasing" : "Purchase"}
          </button>
        </aside>
      )}
      {!!cartItems?.length ? (
        <ProductList />
      ) : (
        <h1>Cart is Empty!</h1>
      )}
    </div>
  );
};

export default CartPage;
