import React, { useState } from "react";
import styles from "./ProductDetails.module.css";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import MinusIcon from "../../../UI/Icons/MinusIcon";
import PlusIcon from "../../../UI/Icons/PlusIcon";
import {
  addProductsToCart,
  removeProductFromCart,
  updateProductQuantityInCart,
} from "../../../../redux/actions/cartActions";
import { authSelector } from "../../../../redux/reducers/authReducer";

const ProductDetails = ({ onRemove, product, title, price, productId, onCart, quantity }) => {
  const [productAddingToCart, setProductAddingToCart] = useState(false);
  const [productRemovingFromCart, setProductRemovingCart] = useState(false);
  const [currentQty, setCurrentQty] = useState(quantity || 1);

  const { user } = useSelector(authSelector);
  const dispatch = useDispatch();
  const uid = user?.uid;

  const handleAddProductToCart = async () => {
    try {
      setProductAddingToCart(true);
      await dispatch(addProductsToCart(product, uid));
    } finally {
      setProductAddingToCart(false);
    }
  };

  const handleRemoveProductFromCart = async () => {
    try {
      setProductRemovingCart(true);
      if (onRemove) {
        await onRemove(productId);
      } else {
        await dispatch(removeProductFromCart(productId, uid));
      }
    } finally {
      setProductRemovingCart(false);
    }
  };

  const handleAddProductQty = async () => {
    const newQty = currentQty + 1;
    setCurrentQty(newQty);
    await dispatch(updateProductQuantityInCart(productId, newQty, uid));
  };

  const handleRemoveProductQty = async () => {
    if (currentQty <= 1) return; // Prevent below 1
    const newQty = currentQty - 1;
    setCurrentQty(newQty);
    await dispatch(updateProductQuantityInCart(productId, newQty, uid));
  };

  return (
    <div className={styles.productDetails}>
      <div className={styles.productName}>
        <p>{`${title.slice(0, 35)}...`}</p>
      </div>

      <div className={styles.productOptions}>
        <p>â‚¹ {price}</p>
        {onCart && (
          <div className={styles.quantityContainer}>
            <MinusIcon handleRemove={handleRemoveProductQty} />
            {currentQty}
            <PlusIcon handleAdd={handleAddProductQty} />
          </div>
        )}
      </div>

      {!onCart ? (
        <button
          className={styles.addBtn}
          title="Add to Cart"
          disabled={productAddingToCart}
          onClick={handleAddProductToCart}
        >
          {productAddingToCart ? "Adding..." : "Add To Cart"}
        </button>
      ) : (
        <button
          className={styles.removeBtn}
          title="Remove from Cart"
          disabled={productRemovingFromCart}
          onClick={handleRemoveProductFromCart}
        >
          {productRemovingFromCart ? "Removing..." : "Remove From Cart"}
        </button>
      )}
    </div>
  );
};

export default ProductDetails;
